"""Some standard RGB colour tuples.

   The tuples defined here are based on the sixteen named colours
   from the HTML 4.01 specification."""


__author__  = 'Nick Efford'
__version__ = '0.4'


WHITE   = (255, 255, 255)
SILVER  = (191, 191, 191)
GREY    = (128, 128, 128)
BLACK   = (  0,   0,   0)
RED     = (255,   0,   0)
MAROON  = (128,   0,   0)
YELLOW  = (255, 255,   0)
OLIVE   = (128, 128,   0)
LIME    = (  0, 255,   0)
GREEN   = (  0, 128,   0)
AQUA    = (  0, 255, 255)
TEAL    = (  0, 128, 128)
BLUE    = (  0,   0, 255)
NAVY    = (  0,   0, 128)
FUCHSIA = (255,   0, 255)
PURPLE  = (128,   0, 128)
