"""Utils for testing."""

import contextlib
import shutil
import sys
import tempfile
import uuid

import six


def encode_utf8(s):
    return six.binary_type(six.u(s).encode("utf-8"))


class AddressGenerationMixin(object):

    def setUp(self):
        self.tempdir = tempfile.mkdtemp()

    def tearDown(self):
        shutil.rmtree(self.tempdir)

    def genAddress(self):
        return "ipc://" + self.tempdir + "/pycom_test-%d" % uuid.uuid4().int


